'use strict';

// MODULES //

var sqrt = require( '@stdlib/math/base/special/sqrt' );
var abs = require( '@stdlib/math/base/special/abs' );
var pow = require( '@stdlib/math/base/special/pow' );


// MAIN //

/**
* Compute the 2-norm of `x`.
*
* @param {PositiveInteger} N - number of values over which to copmute the 2-norm.
* @param {Float32Array} x - input array.
* @param {PositiveInteger} stride - `x` stride length.
* @returns {number} 2-norm of `x`
*
* @example
* var Float32Array = require( '@stdlib/array/float32' );
*
* var x = new Float32Array( [ 1.0, -2.0, 2.0 ] );
* var N = 3;
*
* var z = snrm2( N, x, 1 );
* // returns 3.0
*/
function snrm2( N, x, stride ) {
	var absxi;
	var scale;
	var ssq;
	var ix;
	var i;

	if ( N <= 0 ) {
		return 0.0;
	}

	if ( stride <= 0 ) {
		return 0.0;
	}

	if ( N === 1 ) {
		return abs( x[ 0 ] );
	}

	ix = 0;
	scale = 0.0;
	ssq = 1.0;
	for ( i = 0; i < N; i += 1 ) {
		if ( x[ ix ] !== 0.0 ) {
			absxi = abs( x[ ix ] );
			if ( scale < absxi ) {
				ssq = 1.0 + ( ssq * pow( scale / absxi, 2 ) );
				scale = absxi;
			} else {
				ssq += pow( absxi / scale, 2 );
			}
		}
		ix += stride;
	}

	return scale * sqrt( ssq );
}


// EXPORTS //

module.exports = snrm2;
